import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import { Link, useParams } from 'react-router-dom';
import { ContactService } from "../../services/ContactService";

let ViewContact = () => {

    let {contactId} = useParams();

    let [state, setState] = useState({
        loading: false,
        contact: {},
        errorMessage: ''
    });

    const loadData = async() => {
        try {
            setState({...state, loading: true});
            let response = await ContactService.getContact(contactId);
            setState({
                ...state,
                loading: false,
                contact: response.data
            });
        } catch (error) {
            setState({
                ...state,
                loading: false,
                errorMessage: error.message
            })
        }
    }

    useEffect(async() => {
        loadData();
    }, [contactId]);

    let {loading, contact, errorMessage} = state;

    return (
        <React.Fragment>
            <section className="view-contact-intro p-3">
                <div className="container">
                    <div className="row">
                        <div className="col">
                            <p className="h3 text-warning fw-bold">View Contact</p>
                            <p className="fst-italic">
                                Lorem ipsum dolor sit amet consectetur adipisicing elit. Iusto itaque expedita quos, modi ullam dolor pariatur consequatur eligendi alias facere totam, tempora sint doloribus minus at ex commodi officia? Eligendi.
                            </p>
                        </div>
                    </div>
                </div>
            </section>
            <section className="view-contact mt-3">
                <div className="container">
                    <div className="row align-items-center">
                        <div className="col-md-4">
                            <img src="https://icon-library.com/images/users-icon-png/users-icon-png-15.jpg" alt="" className="img-fluid contact-img" />
                        </div>
                        <div className="col-md-8">
                            <ul className="list-group">
                                <li className="list-group-item list-group-item-action">
                                    Name: <span className="fw-bold">Dai Trong Quach</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Mobile: <span className="fw-bold">0978000000</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Email: <span className="fw-bold">daitrongquach@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Company: <span className="fw-bold">daitrongquach@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Title: <span className="fw-bold">daitrongquach@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Group: <span className="fw-bold">daitrongquach@gmail.com</span>
                                </li>
                                <li className="list-group-item list-group-item-action">
                                    Email: <span className="fw-bold">daitrongquach@gmail.com</span>
                                </li>
                            </ul>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <Link to={'/contacts/list'} className="btn btn-warning">Back</Link>
                        </div>
                    </div>
                </div>
            </section>
        </React.Fragment>
    )
}

export default ViewContact;
