import React, { useState, useEffect } from "react";
import { Link, useParams, useNavigate } from 'react-router-dom';
import { ContactService } from "../../services/ContactService";

let EditContact = () => {

    let navigate = useNavigate();

    let {contactId} = useParams();

    let [state, setState] = useState({
        loading: false,
        contact: {
            name: '',
            photo: '',
            mobile: '',
            email: '',
            company: '',
            title: '',
            groupId: ''
        },
        groups: [],
        errorMessage: ''
    });

    const loadData = async() => {
        try {
            setState({...state, loading: false});
            let response = await ContactService.getContact(contactId);
            let groupResponse = await ContactService.getGroups();
            setState({
                ...state,
                loading: false,
                contact: response.data,
                groups: groupResponse.data
            });
        } catch (error) {
            setState({
                ...state,
                loading: false,
                errorMessage: error.message
            });
        }
    }

    useEffect(() => {
        loadData();
    }, [contactId])

    let updateInput = (event) => {
        setState({
            ...state,
            contact: {
                ...state.contact,
                [event.target.name]: event.target.value
            }
        });
    };

    let submitForm = async(event) => {
        event.prevenDefault();

        try {
            let response = await ContactService.updateContact(state.contact, contactId);
            if (response) {
                navigate('/contacts/list', { replace: true });
            }
        } catch (error) {
            setState({...state, errorMessage: error.message});
            navigate(`/contacts/edit/${contactId}`, { replace: true });
        }
    }

    let {loading, contact, groups, errorMessage} = state;

    return (
        <React.Fragment>
            {
                loading ? <Spinner /> : <React.Fragment>
                    <section className="add-conact p-3">
                        <div className="container">
                            <div className="row">
                                <div className="col">
                                    <p className="h4 text-primary fw-bold">Edit Contact</p>
                                    <p className="fst-italic">
                                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Quidem tempora velit quas impedit sapiente ea error delectus rem nesciunt non earum dolorum fugiat qui, temporibus voluptate! Dolore voluptatum temporibus soluta.
                                    </p>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-md-4">
                                    <form onSubmit={submitForm}>
                                        <div className="mb-2">
                                            <input
                                                required="true"
                                                name="name"
                                                value={contact.name}
                                                onChange={updateInput}
                                                type="text"
                                                className="form-control"
                                                placeholder="Name" />
                                        </div>
                                        <div className="mb-2">
                                            <input 
                                                required="true"
                                                name="photo"
                                                value={contact.photo}
                                                onChange={updateInput}
                                                type="text" className="form-control" placeholder="Photo Url" />
                                        </div>
                                        <div className="mb-2">
                                            <input
                                                required="true"
                                                name="mobile"
                                                value={contact.mobile}
                                                onChange={updateInput}
                                                type="number" className="form-control" placeholder="Mobile" />
                                        </div>
                                        <div className="mb-2">
                                            <input
                                                required="true"
                                                name="email"
                                                value={contact.email}
                                                onChange={updateInput}
                                                type="email" className="form-control" placeholder="Email" />
                                        </div>
                                        <div className="mb-2">
                                            <input
                                                required="true"
                                                name="company"
                                                value={contact.company}
                                                onChange={updateInput}
                                                type="text" className="form-control" placeholder="Company" />
                                        </div>
                                        <div className="mb-2">
                                            <input
                                                required="true"
                                                name="title"
                                                value={contact.title}
                                                onChange={updateInput}
                                                type="text" className="form-control" placeholder="Title" />
                                        </div>
                                        <div className="mb-2">
                                            <select
                                                required="true"
                                                name="groupId"
                                                value={contact.groupId}
                                                onChange={updateInput}
                                                className="form-control">
                                                <option value="">Select a Group</option>
                                                {
                                                    groups.length > 0 && groups.map(group => {
                                                        return (
                                                            <option key={group.id} value={group.id}>{group.name}</option>
                                                        )
                                                    })
                                                }
                                            </select>
                                        </div>
                                        <div className="mb-2">
                                            <input type="submit" className="btn btn-primary" value="Update" />
                                            <Link to={'/contacts/list'} className="btn btn-dark ms-2">Cancel</Link>
                                        </div>
                                    </form>
                                </div>
                                <div className="col-md-6">
                                    <img src={contact.photo} alt="" className="contact-img" />
                                </div>
                            </div>
                        </div>
                    </section>
                </React.Fragment>
            }
        </React.Fragment>
    )
}

export default EditContact;
